select (select sum(i.depositoinversion) from inversion i where i.fechainversion>='2012-01-01' and i.fechainversion <='2012-01-31' and tipoinversionid not in ('PS2','PSO','K3','PSV') and i.inversionanteriorid is null) as Enero,(select sum(i.depositoinversion) from inversion i where i.fechainversion>='2012-02-01' and i.fechainversion <='2012-02-29' and tipoinversionid not in ('PS2','PSO','K3','PSV') and i.inversionanteriorid is null) as Feberero,(select sum(i.depositoinversion) from inversion i where i.fechainversion>='2012-03-01' and i.fechainversion <='2012-03-31' and tipoinversionid not in ('PS2','PSO','K3','PSV') and i.inversionanteriorid is null) as Marzo, (select sum(i.depositoinversion) from inversion i where i.fechainversion>='2012-04-01' and i.fechainversion <='2012-04-30' and tipoinversionid not in ('PS2','PSO','K3','PSV') and i.inversionanteriorid is null) as Abril,(select sum(i.depositoinversion) from inversion i where i.fechainversion>='2012-05-01' and i.fechainversion <='2012-05-31' and tipoinversionid not in ('PS2','PSO','K3','PSV') and i.inversionanteriorid is null) as Mayo, (select sum(i.depositoinversion) from inversion i where i.fechainversion>='2012-06-01' and i.fechainversion <='2012-06-30' and tipoinversionid not in ('PS2','PSO','K3','PSV') and i.inversionanteriorid is null) as Junio, (select sum(i.depositoinversion) from inversion i where i.fechainversion>='2012-07-01' and i.fechainversion <='2012-07-31' and tipoinversionid not in ('PS2','PSO','K3','PSV') and i.inversionanteriorid is null) as Julio, (select sum(i.depositoinversion) from inversion i where i.fechainversion>='2012-08-01' and i.fechainversion <='2012-08-31' and tipoinversionid not in ('PS2','PSO','K3','PSV') and i.inversionanteriorid is null) as Agosto, (select sum(i.depositoinversion) from inversion i where i.fechainversion>='2012-09-01' and i.fechainversion <='2012-09-30' and tipoinversionid not in ('PS2','PSO','K3','PSV') and i.inversionanteriorid is null) as Septiembre, (select sum(i.depositoinversion) from inversion i where i.fechainversion>='2012-10-01' and i.fechainversion <='2012-10-31' and tipoinversionid not in ('PS2','PSO','K3','PSV') and i.inversionanteriorid is null) as Octubre, (select sum(i.depositoinversion) from inversion i where i.fechainversion>='2012-11-01' and i.fechainversion <='2012-11-30' and tipoinversionid not in ('PS2','PSO','K3','PSV') and i.inversionanteriorid is null) as Noviembre, (select sum(i.depositoinversion) from inversion i where i.fechainversion>='2012-12-01' and i.fechainversion <='2012-12-31' and tipoinversionid not in ('PS2','PSO','K3','PSV') and i.inversionanteriorid is null) as Diciembre;
