insert into `examenes_preguntas` (`id_examen`, `pregunta_valor`, `respuesta1`, `respuesta2`, `respuesta3`, `respuesta4`, `respuesta_correcta`) values (4,'¿Es el medio que comúnmente es utilizado para minimizar un riesgo por parte del deudor ante el incumplimiento de sus obligaciones contraídas?','Aval','Firma solidaria ','Garantías','Firma mancomunada','C');
insert into `examenes_preguntas` (`id_examen`, `pregunta_valor`, `respuesta1`, `respuesta2`, `respuesta3`, `respuesta4`, `respuesta_correcta`) values (4,'¿Para la Cooperativa cómo se clasifican las Garantías?','Garantías prendarias, Garantías Mixtas, Dación en pago','Garantías Reales (Hipotecarias y/o prendarias), Dación en pago, Garantías Mixtas','Garantías Personales , Garantías Reales (Hipotecarias y/o prendarias), Garantías Mixtas','Firma de avales , Firmas mancomunadas, Garantías reales','C');
