insert into `examenes_preguntas` (`id_examen`, `pregunta_valor`, `respuesta1`, `respuesta2`, `respuesta3`, `respuesta4`, `respuesta_correcta`) values (4,'¿Cuándo se procede a la liberación de Garantías?','A  petición del socio por escrito','Ante la liquidación total del adeudo','Autorización del Gerente Comercial','Autorización del Gerente General','B');
insert into `examenes_preguntas` (`id_examen`, `pregunta_valor`, `respuesta1`, `respuesta2`, `respuesta3`, `respuesta4`, `respuesta_correcta`) values (4,'¿En que casos se procede a la Exigibilidad de las Garantías?','Incumplimiento en el deposito de la garantía liquida, Falta de pago en las amortizaciones de capital e intereses, Incumplimiento en el registro del Contrato de mutuo','Incumplimiento de pago en las amortizaciones de capital e intereses, Desviación en la aplicación de los recursos según el proyecto definido., Disminución en el valor registrado de las garantías','Falta de pago en las amortizaciones de capital e intereses, Cambio de domicilio del propietario de la vivienda','Cambio de domicilio del propietario de la vivienda, Incumplimiento en el registro del Contrato de mutuo','B');
