select (select sum(haber) from movipolizas mp, polizas p where mp.polizaid=p.polizaid and cuentaid in ('21010204','21010201','21010206','21010207','21010208','21010210','21010209','21010205','21010203','21010202','21010211') and p.fechapoliza between '2013-01-01' and '2013-01-31' and seriepoliza not in ('ZA')) as Enero, (select sum(haber) from movipolizas mp, polizas p where mp.polizaid=p.polizaid and cuentaid in ('21010204','21010201','21010206','21010207','21010208','21010210','21010209','21010205','21010203','21010202','21010211') and p.fechapoliza between '2013-02-01' and '2013-02-28' and seriepoliza not in ('ZA')) as Febrero, (select sum(haber) from movipolizas mp, polizas p where mp.polizaid=p.polizaid and cuentaid in ('21010204','21010201','21010206','21010207','21010208','21010210','21010209','21010205','21010203','21010202','21010211') and p.fechapoliza between '2013-03-01' and '2013-03-31' and seriepoliza not in ('ZA')) as Marzo, (select sum(haber) from movipolizas mp, polizas p where mp.polizaid=p.polizaid and cuentaid in ('21010204','21010201','21010206','21010207','21010208','21010210','21010209','21010205','21010203','21010202','21010211') and p.fechapoliza between '2013-04-01' and '2013-04-30' and seriepoliza not in ('ZA')) as Abril,(select sum(haber) from movipolizas mp, polizas p where mp.polizaid=p.polizaid and cuentaid in ('21010204','21010201','21010206','21010207','21010208','21010210','21010209','21010205','21010203','21010202','21010211') and p.fechapoliza between '2013-05-01' and '2013-05-31' and seriepoliza not in ('ZA')) as Mayo,(select sum(haber) from movipolizas mp, polizas p where mp.polizaid=p.polizaid and cuentaid in ('21010204','21010201','21010206','21010207','21010208','21010210','21010209','21010205','21010203','21010202','21010211') and p.fechapoliza between '2013-06-01' and '2013-06-30' and seriepoliza not in ('ZA')) as Junio,(select sum(haber) from movipolizas mp, polizas p where mp.polizaid=p.polizaid and cuentaid in ('21010204','21010201','21010206','21010207','21010208','21010210','21010209','21010205','21010203','21010202','21010211') and p.fechapoliza between '2013-07-01' and '2013-07-31' and seriepoliza not in ('ZA')) as Julio,(select sum(haber) from movipolizas mp, polizas p where mp.polizaid=p.polizaid and cuentaid in ('21010204','21010201','21010206','21010207','21010208','21010210','21010209','21010205','21010203','21010202','21010211') and p.fechapoliza between '2013-08-01' and '2013-08-30' and seriepoliza not in ('ZA')) as Agosto,(select sum(haber) from movipolizas mp, polizas p where mp.polizaid=p.polizaid and cuentaid in ('21010204','21010201','21010206','21010207','21010208','21010210','21010209','21010205','21010203','21010202','21010211') and p.fechapoliza between '2013-09-01' and '2013-09-30' and seriepoliza not in ('ZA')) as Septiembre,(select sum(haber) from movipolizas mp, polizas p where mp.polizaid=p.polizaid and cuentaid in ('21010204','21010201','21010206','21010207','21010208','21010210','21010209','21010205','21010203','21010202','21010211') and p.fechapoliza between '2013-10-01' and '2013-10-30' and seriepoliza not in ('ZA')) as Octubre,(select sum(haber) from movipolizas mp, polizas p where mp.polizaid=p.polizaid and cuentaid in ('21010204','21010201','21010206','21010207','21010208','21010210','21010209','21010205','21010203','21010202','21010211') and p.fechapoliza between '2013-11-01' and '2013-11-30' and seriepoliza not in ('ZA')) as Noviembre,(select sum(haber) from movipolizas mp, polizas p where mp.polizaid=p.polizaid and cuentaid in ('21010204','21010201','21010206','21010207','21010208','21010210','21010209','21010205','21010203','21010202','21010211') and p.fechapoliza between '2013-12-01' and '2013-12-31' and seriepoliza not in ('ZA')) as Diciembre;


