select (select sum(i.depositoinversion) from inversion i where i.fechainversion>='2013-01-01' and i.fechainversion <='2013-01-31' and tipoinversionid  in ('PS2','PSO','PSV') and i.inversionanteriorid is null) as Enero,(select sum(i.depositoinversion) from inversion i where i.fechainversion>='2013-02-01' and i.fechainversion <='2013-02-28' and tipoinversionid  in ('PS2','PSO','PSV') and i.inversionanteriorid is null) as Feberero,(select sum(i.depositoinversion) from inversion i where i.fechainversion>='2013-03-01' and i.fechainversion <='2013-03-31' and tipoinversionid  in ('PS2','PSO','PSV') and i.inversionanteriorid is null) as Marzo, (select sum(i.depositoinversion) from inversion i where i.fechainversion>='2013-04-01' and i.fechainversion <='2013-04-30' and tipoinversionid  in ('PS2','PSO','PSV') and i.inversionanteriorid is null) as Abril,(select sum(i.depositoinversion) from inversion i where i.fechainversion>='2013-05-01' and i.fechainversion <='2013-05-31' and tipoinversionid  in ('PS2','PSO','PSV') and i.inversionanteriorid is null) as Mayo, (select sum(i.depositoinversion) from inversion i where i.fechainversion>='2013-06-01' and i.fechainversion <='2013-06-30' and tipoinversionid  in ('PS2','PSO','PSV') and i.inversionanteriorid is null) as Junio, (select sum(i.depositoinversion) from inversion i where i.fechainversion>='2013-07-01' and i.fechainversion <='2013-07-31' and tipoinversionid  in ('PS2','PSO','PSV') and i.inversionanteriorid is null) as Julio, (select sum(i.depositoinversion) from inversion i where i.fechainversion>='2013-08-01' and i.fechainversion <='2013-08-31' and tipoinversionid  in ('PS2','PSO','PSV') and i.inversionanteriorid is null) as Agosto, (select sum(i.depositoinversion) from inversion i where i.fechainversion>='2013-09-01' and i.fechainversion <='2013-09-30' and tipoinversionid  in ('PS2','PSO','PSV') and i.inversionanteriorid is null) as Septiembre, (select sum(i.depositoinversion) from inversion i where i.fechainversion>='2013-10-01' and i.fechainversion <='2013-10-31' and tipoinversionid  in ('PS2','PSO','PSV') and i.inversionanteriorid is null) as Octubre, (select sum(i.depositoinversion) from inversion i where i.fechainversion>='2013-11-01' and i.fechainversion <='2013-11-30' and tipoinversionid  in ('PS2','PSO','PSV') and i.inversionanteriorid is null) as Noviembre, (select sum(i.depositoinversion) from inversion i where i.fechainversion>='2013-12-01' and i.fechainversion <='2013-12-31' and tipoinversionid  in ('PS2','PSO','PSV') and i.inversionanteriorid is null) as Diciembre;