select (select clavesocioint from socio where socioid=mc.socioid),(select su.nombre||' '||su.paterno||' '||su.materno from sujeto su where su.sujetoid = (select sujetoid from socio where socioid=mc.socioid)) as nombre,sum(debe),(select coalesce(saldomov,0) from saldomov(mc.socioid,'PA','2014-06-30')) from movicaja mc, movipolizas mp, polizas p where mc.polizaid=p.polizaid and mc.movipolizaid=mp.movipolizaid and mc.tipomovimientoid='PA' and p.fechapoliza between '2014-06-01' and '2014-06-30' and debe>0 group by socioid,p.fechapoliza;