select s.clavesocioint,su.nombre||' '||su.paterno||' '||su.materno as nombresocio,i.depositoinversion,(select * from spsfoliopsinversion(i.inversionid)) from socio s, sujeto su,inversion i where i.tipoinversionid='PSO' and i.depositoinversion<>i.retiroinversion and s.socioid=i.socioid and s.sujetoid=su.sujetoid;

select s.clavesocioint,su.nombre||' '||su.paterno||' '||su.materno as nombresocio,cp.fechadegeneracion as fecha_corte,i.depositoinversion as Monto_PSO,trunc(i.depositoinversion/500.00) as Partes_Sociales,i.fechainversion as fechareinversion,(case when i.inversionanteriorid is null then 'NUEVA' else 'REINVERSION' end),(select * from spsfoliopsinversion(i.inversionid)) AS folios from socio s, sujeto su,captaciontotal cp,inversion i where cp.fechadegeneracion='2014-06-30' and cp.inversionid=i.inversionid and s.socioid=i.socioid and s.sujetoid=su.sujetoid and i.tipoinversionid='PSO';