select re.relacionadosid,s.clavesocioint,su.nombre||' '||su.paterno||' '||su.materno,re.parentesco,(select s.clavesocioint from sujeto su,socio s where su.sujetoid=re.sujetoid and su.sujetoid=s.sujetoid),(select su.nombre||' '||su.paterno||' '||su.materno from sujeto su where su.sujetoid=re.sujetoid) from relacionados re, sujeto su, socio s where s.sujetoid=su.sujetoid and re.socioidem=s.socioid;