altas de socios
select  substring((s.clavesocioint),1,4) as clavesuc, s.clavesocioint,si.grupo,substring(su.nombre||' '||su.paterno||' '||su.materno,1,30) as nombresocio,substring((d.calle)||' '||(d.numero_ext),1,30) as domicilio, d.comunidad, d.codpostal as cp ,s.fechaalta,s.fechabaja, s.motivobajaid as descripcionmotivo, s.tiposocioid as tiposocio, s.estatussocio,sexo(si.sexo), su.fecha_nacimiento,si.ocupacion from socio s, sujeto su, solicitudingreso si, domicilio d  where s.fechaalta  >=  '2013-04-01' and s.fechaalta <= '2013-04-30'and s.tiposocioid='02' and  s.socioid=si.socioid and su.sujetoid = s.sujetoid and d.sujetoid=s.sujetoid order by s.fechaalta;

bajas de socios

select substring((s.clavesocioint),1,4) as clavesuc, s.clavesocioint,si.grupo,substring(su.nombre||' '||su.paterno||' '||su.materno,1,30) as nombresocio,substring((d.calle)||' '||(d.numero_ext),1,30) as domicilio, d.comunidad, d.codpostal as cp, s.fechaalta,s.fechabaja,ba.descripcionmotivo, s.tiposocioid as tiposocio, s.estatussocio,s.estatussocio,sexo(si.sexo), su.fecha_nacimiento,si.ocupacion  from socio s, sujeto su, solicitudingreso si, domicilio d, motivobaja ba  where s.fechabaja >= '2013-04-01' and s.fechabaja <= '2013-04-30'  and s.tiposocioid='02' and s.socioid=si.socioid and su.sujetoid = s.sujetoid and d.sujetoid=s.sujetoid and s.motivobajaid=ba.motivobajaid order by si.grupo,s.fechaalta;
