select s.clavesocioint, su.paterno, su.materno, su.nombre, su.fecha_nacimiento, d.calle||' '||d.numero_ext||', '||c.nombrecolonia as direccion, cd.nombreciudadmex as poblacion, e.nombreestadomex as estado, da.diasatraso, substring((s.clavesocioint),1,3) as clavesuc, p.referenciaprestamo,p.montoprestamo,p.saldoprestamo,p.fecha_otorga,p.fecha_vencimiento,p.tasanormal,p.tasa_moratoria,p.fecha_1er_pago, da.diasatraso as diasmora from socio s, sujeto su, solicitudingreso si, domicilio d, colonia c, ciudadesmex cd, estadosmex e, prestamos p, (select diasatraso, prestamoid from spscarteracomunidad('0','ZZZ','2011-12-31')) da where s.sujetoid=su.sujetoid and s.sujetoid=si.sujetoid and s.sujetoid=d.sujetoid and d.coloniaid=c.coloniaid and d.ciudadmexid=cd.ciudadmexid and cd.estadomexid=e.estadomexid and p.socioid=s.socioid and p.claveestadocredito<>'008' and da.prestamoid = p.prestamoid and diasatraso >90 order by diasatraso;
